mapper.sql:
/* sqlColumns start */
IDX , SEQ , BBS_CD , TITLE , CONTENT , UPR_SEQ , RD_CNT , DTH , DEL_YN , USR_ID , CRE_DT , CRE_DT_FORMAT_DT , CRE_DT_FORMAT_TM , MOD_DT , MOD_DT_FORMAT_DT , MOD_DT_FORMAT_TM , LST_DT , LST_DT_FORMAT_DT , LST_DT_FORMAT_TM
/* sqlColumns end */

/* sqlSelectBbs start */
SELECT SEQ , BBS_CD , TITLE , CONTENT , UPR_SEQ , RD_CNT , DTH , DEL_YN , USR_ID , CRE_DT , TO_CHAR(CRE_DT, 'YYYY-MM-DD') AS CRE_DT_FORMAT_DT , TO_CHAR(CRE_DT, 'YYYY-MM-DD HH24:MI:SS') AS CRE_DT_FORMAT_TM , MOD_DT , TO_CHAR(MOD_DT, 'YYYY-MM-DD') AS MOD_DT_FORMAT_DT , TO_CHAR(MOD_DT, 'YYYY-MM-DD HH24:MI:SS') AS MOD_DT_FORMAT_TM , LST_DT , TO_CHAR(LST_DT, 'YYYY-MM-DD') AS LST_DT_FORMAT_DT , TO_CHAR(LST_DT, 'YYYY-MM-DD HH24:MI:SS') AS LST_DT_FORMAT_TM FROM ( SELECT SEQ , BBS_CD , TITLE , CONTENT , UPR_SEQ , RD_CNT , DTH , DEL_YN , USR_ID , CRE_DT , MOD_DT , NVL(MOD_DT, CRE_DT) AS LST_DT FROM TB_BBS )
/* sqlSelectBbs end */

/* sql VWBbs start */
WITH VW_BBS AS ( SELECT ROW_NUMBER() OVER(ORDER BY SEQ) AS IDX , SEQ , BBS_CD , TITLE , CONTENT , UPR_SEQ , RD_CNT , DTH , DEL_YN , USR_ID , CRE_DT , TO_CHAR(CRE_DT, 'YYYY-MM-DD') AS CRE_DT_FORMAT_DT , TO_CHAR(CRE_DT, 'YYYY-MM-DD HH24:MI:SS') AS CRE_DT_FORMAT_TM , MOD_DT , TO_CHAR(MOD_DT, 'YYYY-MM-DD') AS MOD_DT_FORMAT_DT , TO_CHAR(MOD_DT, 'YYYY-MM-DD HH24:MI:SS') AS MOD_DT_FORMAT_TM , LST_DT , TO_CHAR(LST_DT, 'YYYY-MM-DD') AS LST_DT_FORMAT_DT , TO_CHAR(LST_DT, 'YYYY-MM-DD HH24:MI:SS') AS LST_DT_FORMAT_TM FROM ( SELECT SEQ , BBS_CD , TITLE , CONTENT , UPR_SEQ , RD_CNT , DTH , DEL_YN , USR_ID , CRE_DT , MOD_DT , NVL(MOD_DT, CRE_DT) AS LST_DT FROM TB_BBS WHERE 1=1 AND BBS_CD = 'NONE' ) )
/* sql VWBbs end */


/* sqlSearch start */



		AND TITLE LIKE '%' || #{searchText} || '%'


		AND TITLE LIKE '%' || #{searchText} || '%'



/* sqlSearch end */

mapper.select:

/* selectBbsListCount start */



SELECT COUNT(*) AS CNT FROM VW_BBS

/* selectBbsListCount end */


/* selectBbsListPaging start */



SELECT



FROM VW_BBS

WHERE 1 = 1

AND IDX BETWEEN #{itemStIdx} AND #{itemEdIdx}

ORDER BY IDX DESC

/* selectBbsListPaging end */


/* selectBbs start */



SELECT 



FROM VW_BBS

WHERE SEQ = #{seq}

/* selectBbs end */


/* selectBbsList start */



SELECT 



FROM VW_BBS

WHERE 1=1



ORDER BY IDX DESC

/* selectBbsList end */

mapper.insert:

/* insertBbs start */

INSERT INTO TB_BBS
(
	SEQ


	, BBS_CD

	, TITLE
	, CONTENT

	, UPR_SEQ


	, DTH

	, USR_ID
	, CRE_DT
)
VALUES
(
	TB_BBS_SEQ.NEXTVAL

	, #{bbsCd}

	, #{title}
	, #{content}

	, #{uprSeq}


	, #{dth}

	, #{usrId}
	, SYSDATE
)

/* insertBbs end */


/* updateBbs start */

UPDATE TB_BBS
SET
	BBS_CD = #{bbsCd}
	, TITLE = #{title}
	, CONTENT = #{content}
	, MOD_DT = SYSDATE
WHERE
	SEQ = #{seq}

/* updateBbs end */
